package com.cloud.sbjm.security;/** * 存储用户登录之后的凭证信息 * * @author Administrator */public class UserLoginToken {    private String accountId;    private String userToken;    public UserLoginToken() {    }    public UserLoginToken(String accountId, String userToken) {        this.accountId = accountId;        this.userToken = userToken;    }    public String getAccountId() {        return accountId;    }    public void setAccountId(String accountId) {        this.accountId = accountId;    }    public String getUserToken() {        return userToken;    }    public void setUserToken(String userToken) {        this.userToken = userToken;    }}